import React, { useMemo, useRef, useEffect } from 'react';
import ForceGraph2D from 'react-force-graph-2d';

const GraphBackground = () => {
  const graphRef = useRef();

  const graphData = useMemo(() => {
    const nodes = [];
    const links = [];
    const nodeCount = 250; // Puri screen bharne ke liye nodes badha diye hain

    // 1. IP Nodes Generate karna
    for (let i = 0; i < nodeCount; i++) {
      const ip = `${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}`;
      nodes.push({ id: ip, name: ip, val: 2 });
    }

    // 2. Connections banana (Mesh jaal)
    for (let i = 0; i < nodeCount; i++) {
      const targets = [
        nodes[Math.floor(Math.random() * nodeCount)].id,
        nodes[Math.floor(Math.random() * nodeCount)].id
      ];
      targets.forEach(target => {
        if (nodes[i].id !== target) {
          links.push({ 
            source: nodes[i].id, 
            target: target,
            isCritical: Math.random() > 0.5 // 50% paths red honge
          });
        }
      });
    }
    return { nodes, links };
  }, []);

  useEffect(() => {
    // Isse nodes poori screen par fail jayenge (Spread logic)
    graphRef.current.d3Force('charge').strength(-200);
    graphRef.current.d3Force('center').x(window.innerWidth / 2).y(window.innerHeight / 2);
  }, []);

  return (
    <div className="fixed inset-0 -z-50 bg-black">
      <ForceGraph2D
        ref={graphRef}
        graphData={graphData}
        backgroundColor="#000000"

        // Link Styling: Red Glow paths
        linkColor={(link) => link.isCritical ? "rgba(255, 0, 0, 0.7)" : "rgba(255, 255, 255, 0.05)"}
        linkWidth={(link) => link.isCritical ? 1.5 : 0.5}

        // Red particles flow
        linkDirectionalParticles={(link) => link.isCritical ? 4 : 0}
        linkDirectionalParticleSpeed={0.006}
        linkDirectionalParticleColor={() => "#ff0000"}
        linkDirectionalParticleWidth={3}

        // IP Address aur Node Design
        nodeCanvasObject={(node, ctx, globalScale) => {
          const label = node.id;
          const fontSize = 14 / globalScale;

          // Small Red Node Dot
          ctx.beginPath();
          ctx.arc(node.x, node.y, 2, 0, 2 * Math.PI, false);
          ctx.fillStyle = "#ff0000";
          ctx.fill();

          // IP Text: Har node par IP dikhega
          ctx.font = `${fontSize}px 'JetBrains Mono', monospace`;
          ctx.textAlign = 'center';
          ctx.textBaseline = 'middle';
          ctx.fillStyle = "rgba(255, 0, 0, 0.6)"; // Red IP text

          // Halka zoom par bhi IP dikhega
          if (globalScale > 0.5) {
            ctx.fillText(label, node.x, node.y + 6);
          }
        }}

        // Physics settings taaki ye float karein
        d3AlphaDecay={0.01}
        d3VelocityDecay={0.05}
        warmupTicks={150}
      />
    </div>
  );
};

export default GraphBackground;
