import React, { useState } from 'react';
import axios from 'axios';

const ScannerDashboard = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  const startScan = async () => {
    setLoading(true);
    try {
      const res = await axios.get('http://127.0.0.1:5000/api/start-scan');
      setData(res.data);
    } catch (err) {
      console.error("Scanner Down!", err);
    }
    setLoading(false);
  };

  return (
    <div className="pt-40 min-h-screen bg-black text-white flex flex-col items-center">
      <h1 className="text-4xl font-black mb-10 tracking-widest border-b-4 border-red-600">ADVANCED SCANNER ENGINE</h1>

      <button 
        onClick={startScan}
        className="px-10 py-4 bg-red-600 hover:bg-red-700 font-bold rounded-lg transition-all"
      >
        {loading ? "SCANNING PACKETS..." : "LAUNCH SYSTEM SCAN"}
      </button>

      <div className="mt-12 w-full max-w-5xl border border-red-900/50 bg-zinc-900/40 p-6 rounded-lg font-mono text-sm text-red-500">
        <p className="mb-4 border-b border-red-900 pb-2">>>> LIVE OUTPUT PANEL</p>
        <pre className="overflow-auto max-h-[400px]">
          {data ? JSON.stringify(data, null, 2) : ">>> Waiting for scan initiation..."}
        </pre>
      </div>
    </div>
  );
};

export default ScannerDashboard;
