import React, { useState } from 'react';
import axios from 'axios';

const ScannerDashboard = () => {
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);

  const startScan = async () => {
    setLoading(true);
    try {
      const res = await axios.get('http://127.0.0.1:5000/api/start-scan');
      setResults(res.data);
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  return (
    <div className="p-10 pt-32 min-h-screen bg-black">
      <h2 className="text-4xl font-black mb-10 text-red-600 tracking-tighter">
        ADVANCED SCANNER ENGINE
      </h2>

      <button 
        onClick={startScan}
        className="px-10 py-4 bg-red-600 rounded-full font-bold hover:bg-red-700 transition-all"
      >
        {loading ? "SCANNING IN PROGRESS..." : "LAUNCH SYSTEM SCAN"}
      </button>

      <div className="mt-10 bg-zinc-900/50 border border-red-900/30 rounded-xl p-6 font-mono text-sm text-red-400">
        <h3 className="text-white mb-4 border-b border-white/10 pb-2 uppercase tracking-widest">Live Output</h3>
        <pre className="overflow-auto max-h-[500px]">
          {results ? JSON.stringify(results, null, 2) : ">>> Waiting for scan initiation..."}
        </pre>
      </div>
    </div>
  );
};

export default ScannerDashboard;
