import React, { useMemo, useRef, useEffect } from 'react';
import ForceGraph2D from 'react-force-graph-2d';

const GraphBackground = () => {
  const graphRef = useRef();

  const graphData = useMemo(() => {
    const nodes = [];
    const links = [];
    // Reference jaisa dense look ke liye 500+ nodes
    const nodeCount = 550; 

    for (let i = 0; i < nodeCount; i++) {
      const ip = `${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}`;
      nodes.push({ id: ip, name: ip, val: 1 });
    }

    for (let i = 0; i < nodeCount; i++) {
      // Multiple connections taaki mesh poori screen par faile
      const targetCount = Math.floor(Math.random() * 2) + 1;
      for (let j = 0; j < targetCount; j++) {
        const target = nodes[Math.floor(Math.random() * nodeCount)].id;
        if (nodes[i].id !== target) {
          links.push({ 
            source: nodes[i].id, 
            target: target,
            isCritical: Math.random() > 0.75 
          });
        }
      }
    }
    return { nodes, links };
  }, []);

  useEffect(() => {
    // 1. Center force ko bilkul hataya taaki nodes beech mein na rukein
    graphRef.current.d3Force('center', null);

    // 2. Many-body force ko itna badhaya ki nodes corners tak bhagein
    graphRef.current.d3Force('charge').strength(-60); 

    // 3. Collision force add ki taaki IPs ek dusre ke upar na chadhon
    graphRef.current.d3Force('collide', (node) => 30);
  }, []);

  return (
    <div className="fixed inset-0 z-0 bg-black">
      <ForceGraph2D
        ref={graphRef}
        graphData={graphData}
        backgroundColor="#000000"

        // Lines styling: Thin and subtle
        linkColor={(link) => link.isCritical ? "rgba(255, 0, 0, 0.45)" : "rgba(255, 255, 255, 0.01)"}
        linkWidth={0.5}

        // Red particles flow for attack paths
        linkDirectionalParticles={(link) => link.isCritical ? 3 : 0}
        linkDirectionalParticleSpeed={0.004}
        linkDirectionalParticleColor={() => "#ff0000"}

        nodeCanvasObject={(node, ctx, globalScale) => {
          const label = node.id;
          const fontSize = 11 / globalScale;

          // Small red dot at node
          ctx.beginPath();
          ctx.arc(node.x, node.y, 0.8, 0, 2 * Math.PI);
          ctx.fillStyle = "rgba(255, 0, 0, 0.7)";
          ctx.fill();

          // IP text ko words ki tarah float karwana
          ctx.font = `${fontSize}px 'JetBrains Mono', monospace`;
          ctx.textAlign = 'center';
          ctx.textBaseline = 'middle';

          // IPs mix colors for better visual depth
          ctx.fillStyle = Math.random() > 0.5 ? "rgba(255, 0, 0, 0.35)" : "rgba(255, 255, 255, 0.15)";

          ctx.fillText(label, node.x, node.y + 5);
        }}

        d3AlphaDecay={0.005} // Motion ko slow aur continuous rakhne ke liye
        d3VelocityDecay={0.04}
        warmupTicks={250} // Direct faili hui screen dikhegi
      />
    </div>
  );
};

export default GraphBackground;
