import React, { useMemo } from 'react';
import ForceGraph2D from 'react-force-graph-2d';

const GraphBackground = () => {
  // Mock IP Data: Real integration mein ye backend se aayega
  const graphData = useMemo(() => {
    const ips = [
      "192.168.1.1", "192.168.1.10", "192.168.1.15", 
      "10.0.0.1", "172.16.0.5", "192.168.1.50"
    ];

    const nodes = ips.map(ip => ({
      id: ip,
      name: ip,
      val: Math.random() * 20 + 10 // Node ka size
    }));

    const links = [
      { source: "192.168.1.1", target: "192.168.1.10" },
      { source: "192.168.1.1", target: "192.168.1.15" },
      { source: "192.168.1.10", target: "10.0.0.1" },
      { source: "172.16.0.5", target: "192.168.1.1" },
      { source: "192.168.1.50", target: "192.168.1.15" }
    ];

    return { nodes, links };
  }, []);

  return (
   <div className="fixed inset-0 -z-50 bg-black">
    <ForceGraph2D
      graphData={graphData}
        backgroundColor="#000000"
        nodeLabel="id"
        nodeColor={() => "#4a5568"} // Gray nodes
        linkColor={() => "rgba(255, 255, 255, 0.15)"} // Faint links
        nodeCanvasObject={(node, ctx, globalScale) => {
          const label = node.id;
          const fontSize = 12 / globalScale;
          ctx.font = `${fontSize}px Inter`;
          ctx.textAlign = 'center';
          ctx.textBaseline = 'middle';
          ctx.fillStyle = "rgba(255, 255, 255, 0.5)"; // IP Text color
          ctx.fillText(label, node.x, node.y);
        }}
        linkDirectionalParticles={2} // Moving particles on paths
        linkDirectionalParticleSpeed={0.005}
        d3VelocityDecay={0.3}
      />
    </div>
  );
};

export default GraphBackground;
