import React from 'react';

const Hero = () => {
  return (
    <div className="flex flex-col items-center justify-center text-center px-4 py-20">
      {/* Project Main Title - Extra Large and Bold */}
      <h1 className="text-6xl md:text-7xl font-extrabold text-white mb-4 tracking-tighter">
        NET SCAN : <span className="text-gray-400">Graph Intelligent Attack Path Analyzer</span>
      </h1>

      {/* Teammates Names - Slightly larger */}
      <p className="text-xl text-gray-500 mb-10 font-medium">
        Mridul | Shiva | Akshat
      </p>

      {/* Description Text - Balanced and readable */}
      <p className="max-w-3xl text-gray-400 text-lg md:text-xl leading-relaxed mb-12">
        The IP analysis system is designed to be versatile, with a wide range of applications 
        across various industries. Its ability to detect and mitigate threats in real-time makes it invaluable.
      </p>

      {/* Buttons - Larger and more prominent */}
      <div className="flex gap-6">
        <button className="px-8 py-3 border border-white rounded-full hover:bg-white hover:text-black transition-all text-lg">
          INFORMATION
        </button>
        <button className="px-8 py-3 border border-white rounded-full hover:bg-white hover:text-black transition-all text-lg">
          DATA
        </button>
        <button className="px-10 py-3 bg-white text-black rounded-full font-bold hover:bg-gray-200 transition-all text-lg">
          VULNERABILITY
        </button>
      </div>
    </div>
  );
};

export default Hero;
