import React, { useMemo, useRef, useEffect } from 'react';
import ForceGraph2D from 'react-force-graph-2d';

const GraphBackground = () => {
  const graphRef = useRef();

  const graphData = useMemo(() => {
    const nodes = [];
    const links = [];
    const nodeCount = 200; // Poori screen ke liye nodes

    for (let i = 0; i < nodeCount; i++) {
      const ip = `${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}`;
      nodes.push({ id: ip, name: ip, val: 1 });
    }

    for (let i = 0; i < nodeCount; i++) {
      for (let j = 0; j < 1; j++) {
        const target = nodes[Math.floor(Math.random() * nodeCount)].id;
        if (nodes[i].id !== target) {
          links.push({ 
            source: nodes[i].id, 
            target: target,
            isCritical: Math.random() > 0.6 
          });
        }
      }
    }
    return { nodes, links };
  }, []);

  useEffect(() => {
    // 1. 'charge' strength ko badhaya taaki nodes beech se hat kar sides mein jayein
    graphRef.current.d3Force('charge').strength(-400); 
    // 2. 'link' distance ko badhaya taaki network bada dikhe
    graphRef.current.d3Force('link').distance(100);
    // 3. Center force ko thoda kam kiya taaki nodes corners tak ja sakein
    graphRef.current.d3Force('center').strength(0.05);
  }, []);

  return (
    <div className="fixed inset-0 -z-50 bg-black">
      <ForceGraph2D
        ref={graphRef}
        graphData={graphData}
        backgroundColor="#000000"
        linkColor={(link) => link.isCritical ? "rgba(255, 0, 0, 0.5)" : "rgba(255, 255, 255, 0.03)"}
        linkWidth={(link) => link.isCritical ? 1.2 : 0.5}
        linkDirectionalParticles={(link) => link.isCritical ? 3 : 0}
        linkDirectionalParticleSpeed={0.005}
        linkDirectionalParticleColor={() => "#ff0000"}

        nodeCanvasObject={(node, ctx, globalScale) => {
          const label = node.id;
          const fontSize = 13 / globalScale;

          ctx.beginPath();
          ctx.arc(node.x, node.y, 1.2, 0, 2 * Math.PI, false);
          ctx.fillStyle = "rgba(255, 0, 0, 0.8)";
          ctx.fill();

          ctx.font = `${fontSize}px 'JetBrains Mono', monospace`;
          ctx.textAlign = 'center';
          ctx.textBaseline = 'middle';
          ctx.fillStyle = "rgba(255, 0, 0, 0.4)";

          // IP addresses hamesha visible rahengi par halki (floating feel)
          ctx.fillText(label, node.x, node.y + 5);
        }}

        d3AlphaDecay={0.01}
        d3VelocityDecay={0.08}
        enablePointerInteraction={true} // Taki tum nodes ko mouse se hila sako
      />
    </div>
  );
};

export default GraphBackground;
