import React, { useMemo } from 'react';
import ForceGraph2D from 'react-force-graph-2d';

const GraphBackground = () => {
  const graphData = useMemo(() => {
    const nodes = [];
    const links = [];
    const nodeCount = 150; // Reference image jaisa dense dikhne ke liye 150 nodes

    // 1. Random IPs generate karna
    for (let i = 0; i < nodeCount; i++) {
      const ip = `192.168.${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}`;
      nodes.push({
        id: ip,
        name: ip,
        val: Math.random() * 2 + 1 // Nodes chhote honge toh premium lagenge
      });
    }

    // 2. Random Links (Paths) banana taaki complex network dikhe
    for (let i = 0; i < nodeCount; i++) {
      const targetCount = Math.floor(Math.random() * 3) + 1;
      for (let j = 0; j < targetCount; j++) {
        const target = nodes[Math.floor(Math.random() * nodeCount)].id;
        if (nodes[i].id !== target) {
          links.push({ source: nodes[i].id, target: target });
        }
      }
    }

    return { nodes, links };
  }, []);

  return (
    <div className="fixed inset-0 -z-50 bg-black">
      <ForceGraph2D
        graphData={graphData}
        backgroundColor="#000000"
        nodeLabel="id"
        nodeColor={() => "rgba(255, 255, 255, 0.3)"} // Halki white nodes
        linkColor={() => "rgba(255, 255, 255, 0.05)"} // Bahut patli lines

        // IP text ko sirf tabhi dikhana jab zoom ho, warna bheed ho jayegi
        nodeCanvasObject={(node, ctx, globalScale) => {
          const label = node.id;
          const fontSize = 14 / globalScale;
          ctx.font = `${fontSize}px Inter`;
          ctx.textAlign = 'center';
          ctx.textBaseline = 'middle';
          ctx.fillStyle = "rgba(255, 255, 255, 0.2)"; 
          if (globalScale > 3) ctx.fillText(label, node.x, node.y); // Zoom karne par hi text dikhega

          // Chhoti dot hamesha dikhegi
          ctx.beginPath();
          ctx.arc(node.x, node.y, 1.5, 0, 2 * Math.PI, false);
          ctx.fillStyle = "rgba(255, 255, 255, 0.5)";
          ctx.fill();
        }}

        // Physics Settings taaki graph spread rahe
        d3AlphaDecay={0.01}
        d3VelocityDecay={0.1}
        cooldownTicks={100}
      />
    </div>
  );
};

export default GraphBackground;
