import React, { useMemo } from 'react';
import ForceGraph2D from 'react-force-graph-2d';

const GraphBackground = () => {
  const graphData = useMemo(() => {
    const nodes = [];
    const links = [];
    const nodeCount = 150; // Puri screen bharne ke liye 150 nodes

    // 1. IP Nodes generate karna
    for (let i = 0; i < nodeCount; i++) {
      const ip = `192.168.${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}`;
      nodes.push({
        id: ip,
        name: ip,
        // Nodes ka size random taaki natural lage
        val: Math.random() * 3 + 1 
      });
    }

    // 2. Complex Connections (Links) banana
    for (let i = 0; i < nodeCount; i++) {
      // Har node 1-2 dusri nodes se connect hogi
      const targetCount = Math.floor(Math.random() * 2) + 1;
      for (let j = 0; j < targetCount; j++) {
        const target = nodes[Math.floor(Math.random() * nodeCount)].id;
        if (nodes[i].id !== target) {
          links.push({ 
            source: nodes[i].id, 
            target: target,
            // 30% paths ko "Vulnerable" (High Risk) mark karna
            isVulnerable: Math.random() > 0.7 
          });
        }
      }
    }

    return { nodes, links };
  }, []);

  return (
    <div className="fixed inset-0 -z-50 bg-black">
      <ForceGraph2D
        graphData={graphData}
        backgroundColor="#000000"

        // Link Styling (Red Color logic)
        linkColor={(link) => link.isVulnerable ? "rgba(255, 0, 0, 0.8)" : "rgba(255, 255, 255, 0.1)"}
        linkWidth={(link) => link.isVulnerable ? 1.5 : 0.5}

        // Red paths par chalti hui particles (Attack Path feel)
        linkDirectionalParticles={(link) => link.isVulnerable ? 4 : 0}
        linkDirectionalParticleSpeed={0.005}
        linkDirectionalParticleColor={() => "#ff0000"}
        linkDirectionalParticleWidth={2}

        // IP Address aur Node design
        nodeCanvasObject={(node, ctx, globalScale) => {
          const label = node.id;
          const fontSize = 14 / globalScale;

          // IP Text styling
          ctx.font = `${fontSize}px Inter`;
          ctx.textAlign = 'center';
          ctx.textBaseline = 'middle';
          ctx.fillStyle = "rgba(255, 255, 255, 0.4)";

          // Zoom level ke hisaab se IP dikhana
          if (globalScale > 2) {
            ctx.fillText(label, node.x, node.y + 5);
          }

          // Central Node Dot
          ctx.beginPath();
          ctx.arc(node.x, node.y, 2, 0, 2 * Math.PI, false);
          ctx.fillStyle = "#ff0000"; // Nodes ko bhi red touch diya hai
          ctx.fill();
        }}

        // Physics settings taaki ye poori screen par spread rahe
        d3AlphaDecay={0.01}
        d3VelocityDecay={0.08}
        cooldownTicks={100}
      />
    </div>
  );
};

export default GraphBackground;
