import React, { useMemo, useRef, useEffect } from 'react';
import ForceGraph2D from 'react-force-graph-2d';

const GraphBackground = () => {
  const graphRef = useRef();

  const graphData = useMemo(() => {
    const nodes = [];
    const links = [];
    const nodeCount = 180; // Reference jaisa dense dikhne ke liye

    for (let i = 0; i < nodeCount; i++) {
      const ip = `${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}`;
      nodes.push({ id: ip, name: ip, val: 1 });
    }

    for (let i = 0; i < nodeCount; i++) {
      // Har node ko kam se kam 2 aur nodes se connect karna taaki jaal (mesh) bane
      for (let j = 0; j < 2; j++) {
        const target = nodes[Math.floor(Math.random() * nodeCount)].id;
        if (nodes[i].id !== target) {
          links.push({ 
            source: nodes[i].id, 
            target: target,
            isCritical: Math.random() > 0.6 // 40% paths red honge
          });
        }
      }
    }
    return { nodes, links };
  }, []);

  useEffect(() => {
    // Physics ko force karna taaki graph poori screen par fail jaye
    graphRef.current.d3Force('charge').strength(-150);
    graphRef.current.d3Force('center').x(window.innerWidth / 2).y(window.innerHeight / 2);
  }, []);

  return (
    <div className="fixed inset-0 -z-50 bg-black">
      <ForceGraph2D
        ref={graphRef}
        graphData={graphData}
        backgroundColor="#000000"

        // Link Styling: Red Attack Paths
        linkColor={(link) => link.isCritical ? "rgba(255, 0, 0, 0.6)" : "rgba(255, 255, 255, 0.05)"}
        linkWidth={(link) => link.isCritical ? 1.2 : 0.5}

        // Red Paths par chalti hui particles
        linkDirectionalParticles={(link) => link.isCritical ? 3 : 0}
        linkDirectionalParticleSpeed={0.004}
        linkDirectionalParticleColor={() => "#ff0000"}
        linkDirectionalParticleWidth={2}

        // IP Text aur Node Design
        nodeCanvasObject={(node, ctx, globalScale) => {
          const label = node.id;
          const fontSize = 12 / globalScale;

          // Nodes ko chhota red dot banana
          ctx.beginPath();
          ctx.arc(node.x, node.y, 1, 0, 2 * Math.PI, false);
          ctx.fillStyle = "rgba(255, 0, 0, 0.8)";
          ctx.fill();

          // IP text ko halka red/white dikhana
          ctx.font = `${fontSize}px Inter`;
          ctx.textAlign = 'center';
          ctx.textBaseline = 'middle';
          ctx.fillStyle = "rgba(255, 0, 0, 0.3)";

          // Zoom hone par hi IP dikhega taaki bheed na lage
          if (globalScale > 2.5) {
            ctx.fillText(label, node.x, node.y + 4);
          }
        }}

        // Physics settings: 'd3VelocityDecay' ko kam kiya taaki spread rahe
        d3AlphaDecay={0.01}
        d3VelocityDecay={0.1}
        warmupTicks={100}
      />
    </div>
  );
};

export default GraphBackground;
