import React from 'react';

const Hero = () => {
  return (
    <div className="flex flex-col items-center justify-center text-center py-20 bg-black min-h-screen text-white">
      {/* Bada Bold Heading */}
     <h1 className="text-4xl font-bold text-white">
  NET SCAN VULNERABILITY TRACKER
</h1>
<p className="text-gray-400">
  Mridul| Shiva, and Akshat's Security Assessment Tool
</p>

      {/* Description Text */}
      <p className="mt-8 max-w-2xl text-gray-400 text-lg">
        The IP analysis system is designed to be versatile, with a wide range of applications across various industries. 
        Its ability to detect and mitigate threats in real-time makes it invaluable.
      </p>

      {/* Transparent Pill Buttons */}
      <div className="flex gap-4 mt-12">
        <button className="px-8 py-2 border border-white rounded-full hover:bg-white hover:text-black transition uppercase text-sm tracking-widest">
          Information
        </button>
        <button className="px-8 py-2 border border-white rounded-full hover:bg-white hover:text-black transition uppercase text-sm tracking-widest">
          Data
        </button>
        <button className="px-8 py-2 border border-white rounded-full bg-white text-black uppercase text-sm tracking-widest font-bold">
          Vulnerability
        </button>
      </div>
    </div>
  );
};

export default Hero;
