import React, { useState } from 'react';
import axios from 'axios';
import GraphBackground from './GraphBackground';

const Hero = () => {
  const [isScanning, setIsScanning] = useState(false);
  const [scanData, setScanData] = useState(null);

  const startScan = async () => {
    setIsScanning(true);
    try {
      // Backend (Flask/FastAPI) se scanner trigger karna
      const response = await axios.get('http://localhost:5000/api/start-scan');
      setScanData(response.data);
    } catch (error) {
      console.error("Scan failed:", error);
    }
    setIsScanning(false);
  };

  return (
    <div className="relative w-full min-h-screen flex flex-col items-center justify-center text-center px-6 bg-transparent">
      <GraphBackground />

      <div className="relative z-10">
        {/* Tera existing Hero Text yahan rahega */}

        <div className="flex gap-6 justify-center">
          <button className="px-12 py-4 border border-white/20 rounded-full hover:bg-white hover:text-black transition-all font-black text-[10px] tracking-[0.3em]">
            DATA
          </button>
          <button 
            onClick={startScan}
            className="px-12 py-4 bg-white text-black rounded-full hover:bg-red-600 hover:text-white transition-all font-black text-[10px] tracking-[0.3em] shadow-[0_0_50px_rgba(255,255,255,0.2)]"
          >
            {isScanning ? 'SCANNING...' : 'VULNERABILITY'}
          </button>
        </div>

        {/* üõ†Ô∏è Scanner Result Terminal Integration */}
        {scanData && (
          <div className="mt-10 w-full max-w-4xl bg-black/80 border border-red-900/50 rounded-lg p-6 text-left font-mono text-xs text-red-500 animate-pulse">
            <h3 className="text-white font-bold mb-4 tracking-widest uppercase">Live Attack Path Analysis:</h3>
            <pre className="overflow-auto max-h-60">
              {JSON.stringify(scanData, null, 2)}
            </pre>
          </div>
        )}
      </div>
    </div>
  );
};
