import React, { useMemo, useRef, useEffect } from 'react';
import ForceGraph2D from 'react-force-graph-2d';

const GraphBackground = () => {
  const graphRef = useRef();

  const graphData = useMemo(() => {
    const nodes = [];
    const links = [];
    const nodeCount = 380; // Reference jaisa dense look

    // 1. Dher saari IP Nodes generate karna
    for (let i = 0; i < nodeCount; i++) {
      const ip = `${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}.${Math.floor(Math.random() * 255)}`;
      nodes.push({ id: ip, name: ip, val: 1 });
    }

    // 2. High-Density Mesh Connections
    for (let i = 0; i < nodeCount; i++) {
      const target = nodes[Math.floor(Math.random() * nodeCount)].id;
      if (nodes[i].id !== target) {
        links.push({ 
          source: nodes[i].id, 
          target: target,
          isCritical: Math.random() > 0.7 // 30% Red Attack Paths
        });
      }
    }
    return { nodes, links };
  }, []);

  useEffect(() => {
    // Aggressive Spread Logic: Nodes ko corners mein dhakellne ke liye
    graphRef.current.d3Force('charge').strength(-350); 
    graphRef.current.d3Force('link').distance(70);
    graphRef.current.d3Force('center').strength(0.02); // Middle ko hollow rakhne ke liye
  }, []);

  return (
    // Layering fix taaki background visible rahe
    <div className="fixed inset-0 z-0 bg-black">
      <ForceGraph2D
        ref={graphRef}
        graphData={graphData}
        backgroundColor="#000000"

        // Attack Path Styling
        linkColor={(link) => link.isCritical ? "rgba(255, 0, 0, 0.5)" : "rgba(255, 255, 255, 0.02)"}
        linkWidth={(link) => link.isCritical ? 1.5 : 0.5}
        linkDirectionalParticles={(link) => link.isCritical ? 4 : 0}
        linkDirectionalParticleSpeed={0.007}
        linkDirectionalParticleColor={() => "#ff0000"}

        nodeCanvasObject={(node, ctx, globalScale) => {
          const label = node.id;
          const fontSize = 12 / globalScale;

          // Node Dot
          ctx.beginPath();
          ctx.arc(node.x, node.y, 1.2, 0, 2 * Math.PI);
          ctx.fillStyle = "rgba(255, 0, 0, 0.7)";
          ctx.fill();

          // IP Address Labels
          ctx.font = `${fontSize}px 'JetBrains Mono', monospace`;
          ctx.textAlign = 'center';
          ctx.textBaseline = 'middle';
          ctx.fillStyle = "rgba(255, 0, 0, 0.35)";

          // Har IP visible rahegi but dense network ke beech float karegi
          ctx.fillText(label, node.x, node.y + 5);
        }}

        // Performance & Quick Loading settings
        d3AlphaDecay={0.02} 
        d3VelocityDecay={0.1}
        warmupTicks={100} // Start hote hi spread dikhega
      />
    </div>
  );
};

export default GraphBackground;
