import React, { useState } from 'react';
import axios from 'axios';

const ScannerDashboard = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const startScan = async () => {
    setLoading(true);
    setError(null);
    setData(null); // Purana data clear karega

    try {
      // 1. Localhost ki jagah direct IP use ki hai faster resolution ke liye
      // 2. Timeout add kiya hai taaki 10 sec se zyada na latke
      const res = await axios.get('http://127.0.0.1:5000/api/start-scan', {
        timeout: 10000 
      });

      setData(res.data);
    } catch (err) {
      console.error("Scanner Down!", err);
      setError(err.code === 'ECONNABORTED' ? "Request Timeout: Backend taking too long." : "Backend Server Offline. Please check your Terminal.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="pt-40 min-h-screen bg-black text-white flex flex-col items-center selection:bg-red-600">
      {/* Title with Scan Animation */}
      <h1 className={`text-4xl font-black mb-10 tracking-widest border-b-4 border-red-600 ${loading ? 'animate-pulse text-red-500' : ''}`}>
        ADVANCED SCANNER ENGINE
      </h1>

      <button 
        onClick={startScan}
        disabled={loading}
        className={`px-10 py-4 font-bold rounded-lg transition-all duration-300 transform active:scale-95 ${
          loading 
          ? "bg-zinc-800 text-zinc-500 cursor-not-allowed" 
          : "bg-red-600 hover:bg-red-700 hover:shadow-[0_0_25px_rgba(220,38,38,0.5)]"
        }`}
      >
        {loading ? ">>> INITIALIZING ENGINE..." : "LAUNCH SYSTEM SCAN"}
      </button>

      {/* Live Terminal Output Panel */}
      <div className="mt-12 w-full max-w-5xl border border-red-900/50 bg-black/60 backdrop-blur-md p-6 rounded-lg font-mono text-sm shadow-2xl">
        <div className="flex justify-between mb-4 border-b border-red-900/50 pb-2">
          <p className="text-red-500 font-bold tracking-widest">>>> LIVE OUTPUT PANEL</p>
          <div className="flex gap-2">
            <span className="w-3 h-3 rounded-full bg-red-900 animate-pulse"></span>
            <span className="w-3 h-3 rounded-full bg-red-600"></span>
          </div>
        </div>

        <pre className="overflow-auto max-h-[450px] custom-scrollbar leading-relaxed text-red-500/90">
          {loading && (
            <div className="animate-pulse">
              [SYSTEM] Establishing connection to main.py...<br/>
              [SYSTEM] Running suaabprocess engine.py...<br/>
              [SYSTEM] Parsing network packets from scan_output.json...
            </div>
          )}

          {error && <span className="text-white bg-red-600 px-2 py-1 uppercase">{error}</span>}

          {data && (
            <div className="text-green-400">
              {JSON.stringify(data, null, 2)}
            </div>
          )}

          {!data && !loading && !error && ">>> WAITING FOR SCAN INITIATION... COMMAND: RUN SCAN"}
        </pre>
      </div>
    </div>
  );
};

export default ScannerDashboard;
